var mysql= require("mysql");
var settings= require("settings");

exports.excuteSql=function(sql, callback){

    var conn= mysql.createConnection(settings.dbConfig);
    conn.connect(function (err){
        if (err) throw err;
        conn.query(sql,function(err,rows){
            if(err) throw err;
            callback(rows);
        });
      });
}